import { useState, useEffect} from "react";
import { ZOHO } from "../assets/Zoho";
import { useZohoContext } from "../Context/ZohoContext";

const EditarContactoStore = (props) => {

    const {stores} = useZohoContext();
    const {contactos} = useZohoContext();
    const [record, setRecord] = useState();
    const [id, setId] = useState();
    const [edit, setEdit] = useState(false);
    const contactosStore = contactos.filter(contacto => (contacto.Rol_Contacto.some(rol => rol.includes("Store Decision Maker")|| rol.includes("Fotografía")|| rol.includes("Credenciales"))));



    useEffect(() => {
        console.log(stores)
    }, [stores])

    const searchStore = ()=>{
        let arrayStores = stores.filter( store => store.id == id);
        let id = arrayStores[0].id;
        ZOHO.CRM.API.getRecord({Entity:"Accounts",RecordID:id}).then((result)=>{
            setRecord(result.data[0])
         })
        setEdit(true)
    }
    
    
    const handleInputChage = (e) =>{
        console.log(e.target.value);
        setId(e.target.value);
    }
    return ( 
        
        <div>
            <div class="input-group mb-3">
                <span class="input-group-text" id="basic-addon1">Store ID</span>
                <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" onChange={(event)=>{handleInputChage(event)}}/>
                <button class="btn btn-outline-secondary" type="button" id="button-addon1" onClick={()=>searchStore()}>Buscar</button>
            </div>
            {edit?
                <table className="me-3 table table-borderless">
                    <thead>
                        <tr>
                            <th>Contacto</th>
                            <th>Roles</th>
                        </tr>
                    </thead>
                    <tbody>
                            { 
                            contactosStore.map(user => (
                                <tr key={user.Email} className="mt-3">
                                    <td>{user.First_Name} {user.Last_Name}</td>
                                    <td>{user.Rol_Contacto.map(rol=>(
                                        <span>{rol} - </span> 
                                        ))}</td>
                                    <td>
                                        <button className="btn btn-outline-primary" onClick={()=> setEdit(true)} >Editar</button>
                                    </td>
                                </tr>
                            ))
                        } 
                        
                    </tbody>
                </table>:""
            }
        </div>
        
        
     );
}
 
export default EditarContactoStore;
