import { createContext, useContext, useState, useEffect } from "react";
import {ZOHO} from '../assets/Zoho';

const ZohoContext = createContext();

export default function ZohoProvider({children}) {

   const [module, setModule] = useState(null);
   const [entity, setEntity] = useState(null);
   const [id, setID] = useState(null);

   useEffect(() => {

      const initWidget = () => {
         ZOHO.embeddedApp.on("PageLoad", function (data) {
            console.log(data);
            let id = data.EntityId;
            setID(id);
            console.log(id);
            ZOHO.CRM.API.getRecord({Entity:"Deals",RecordID:id}).then((result)=>{
              setInfoCompleta(result.data[0].Informaci_n_de_Contacto_Completa);
               setRecord(result.data[0])
            })
            ZOHO.CRM.API.getRelatedRecords({Entity:"Deals",RecordID:id,RelatedList:"Oportunidades"}).then((data)=>{
               setStores(data.data);
               console.log(data.data);
           })
            
         
         });
         

         ZOHO.embeddedApp.init();
      }

      setTimeout(()=> {
         initWidget();
      }, 1000);
      
   }, []);
   
   return (
      <ZohoContext.Provider value={{module: module, entity: entity, id:id}}>
         {children}
      </ZohoContext.Provider>
   );
};

export const useWidget = () => {

   const context = useContext(ZohoContext);

   if (!context) throw new Error("UseWidget inside ZohoContext");

   const { entity, module, id} = context;

   return {
      entity, module, id
   }
}
