import { createContext, useContext, useState, useEffect } from "react";
import {ZOHO} from '../assets/Zoho';

const ZohoContext = createContext();

export default function ZohoProvider({children}) {

   const [module, setModule] = useState(null);
   const [entity, setEntity] = useState(null);
   const [id, setID] = useState(null);
   const [infoCompleta, setInfoCompleta] = useState(null);
   const [record, setRecord] = useState(null);
   const [stores, setStores] = useState(null);
   const [paises] = useState(["Mexico","Argentina","Brasil","Colombia","Chile","Ecuador","Peru","Uruguay"]);
   const [codigos, setCodigos] = useState({})

   useEffect(() => {

      const initWidget = () => {
         ZOHO.embeddedApp.on("PageLoad", function (data) {
            console.log(data);
            let id = data.EntityId;
            setID(id);
            console.log(id);
            ZOHO.CRM.API.getRecord({Entity:"Deals",RecordID:id}).then((result)=>{
              setInfoCompleta(result.data[0].Informaci_n_de_Contacto_Completa);
               setRecord(result.data[0])
            })
            ZOHO.CRM.API.getRelatedRecords({Entity:"Deals",RecordID:id,RelatedList:"Oportunidades"}).then((data)=>{
               setStores(data.data);
               console.log(data.data);
           })
           let mapPaises = new Object({Mexico:[],
            Argentina:[],
            Brasil:[],
            Colombia:[],
            Chile:[],
            Ecuador:[],
            Peru:[],
            Uruguay:[]});
           paises.forEach(pais => {
               let variable = "codigos_ciudad_"+pais;
               ZOHO.CRM.API.getOrgVariable(variable).then(function(data){
                  //console.log(pais);
                  let array = data.Success.Content;
                  array = array.split(",");
                  //console.log(array)
                  mapPaises[pais]=array;
               }).catch(error => console.error("No se encotró en Zoho la variable "+variable));
           });
           console.log(mapPaises);
           setCodigos(mapPaises)
            
         
         });
         

         ZOHO.embeddedApp.init();
      }

      setTimeout(()=> {
         initWidget();
      }, 1000);
      
   }, []);
   
   return (
      <ZohoContext.Provider value={{module: module, entity: entity, id:id, setID:setID, infoCompleta:infoCompleta, setInfoCompleta:setInfoCompleta, record: record, setRecord:setRecord, stores:stores, setStores:setStores, codigos:codigos}}>
         {children}
      </ZohoContext.Provider>
   );
};

export const useWidget = () => {

   const context = useContext(ZohoContext);

   if (!context) throw new Error("UseWidget inside ZohoContext");

   const { entity, module, id, setID, infoCompleta, setInfoCompleta, record, setRecord, stores, setStores, codigos} = context;

   return {
      entity, module, id, setID, infoCompleta, setInfoCompleta, record, setRecord, stores, setStores, codigos
   }
}
