import {useState, useEffect} from "react";
import { useZohoContext } from "../Context/ZohoContext";
import SearchStores from "./SearchStores";

const OpenWidget= (props) => {

    const{id}=useZohoContext();
    const{stores}=useZohoContext();
    const[mostrar, setMostrar]=useState(false);
    
    useEffect(() => {
        console.log(id)
        
    }, [id])


    const getStores = ()=>{
        //props.setLink("searchStores")
        setMostrar(true);

    }
    const getBrand = ()=>{
        console.log(id)
    }

    return ( 
        <div className="text-center">
            
            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">
                        <button onClick={getBrand} className="btn btn-primary">
                            Brands
                        </button>
                    </th>
                    <th scope="col">
                        <button onClick={getStores} className="btn btn-primary">
                            Stores
                        </button>
                    </th>
                    <th scope="col">
                        <button onClick={getStores} className="btn btn-primary">
                            Raz贸n Social
                        </button>
                    </th>
                    </tr>
                </thead>
                <tbody className="text-center">
                </tbody>
            </table>
            <div class="container">
                <div class="row">
                <button onClick={getStores} className="btn btn-primary col s-2">
                            Raz贸n Social
                        </button>
                        <button onClick={getStores} className="btn btn-primary col s-2">
                            Raz贸n Social
                        </button>
                        <button onClick={getStores} className="btn btn-primary col s-2">
                            Raz贸n Social
                        </button>
                </div>
            </div>

            {mostrar?
            <div className="text-center container">
                <SearchStores/>
            </div>:""}
            
        </div>
        
     );

}
 
export default OpenWidget
;
